Installing service worker
var swsource = "https://t5.shwchurch.org/pwa-amp-sw.js";
if("serviceWorker" in navigator) { navigator.serviceWorker.register(swsource, {scope: 'https://t5.shwchurch.org/amp/'}).then(function(reg){
console.log('Congratulations!!Service Worker Registered ServiceWorker scope: ', reg.scope);
}).catch(function(err) {
console.log('ServiceWorker registration failed: ', err);
}); };
window.addEventListener('beforeinstallprompt', (e) = {
deferredPrompt = e;
})
